mapper.select:

select 

,

from mybook
where xm = #{xm,jdbcType=VARCHAR}
  and fsr = #{fsr,jdbcType=VARCHAR}
  and fsrq = #{fsrq,jdbcType=TIMESTAMP}
  and sfflag = #{sfflag,jdbcType=CHAR}

mapper.delete:

delete from mybook
where xm = #{xm,jdbcType=VARCHAR}
  and fsr = #{fsr,jdbcType=VARCHAR}
  and fsrq = #{fsrq,jdbcType=TIMESTAMP}
  and sfflag = #{sfflag,jdbcType=CHAR}

mapper.update:

update mybook
set lx = #{lx,jdbcType=VARCHAR},
  subject = #{subject,jdbcType=VARCHAR},
  content = #{content,jdbcType=VARCHAR},
  filename = #{filename,jdbcType=VARCHAR},
  zt = #{zt,jdbcType=VARCHAR},
  shrq = #{shrq,jdbcType=TIMESTAMP},
  action = #{action,jdbcType=VARCHAR},
  isread = #{isread,jdbcType=CHAR},
  isok = #{isok,jdbcType=INTEGER},
  isshow = #{isshow,jdbcType=INTEGER},
  pkid = #{pkid,jdbcType=BIGINT}
where xm = #{xm,jdbcType=VARCHAR}
  and fsr = #{fsr,jdbcType=VARCHAR}
  and fsrq = #{fsrq,jdbcType=TIMESTAMP}
  and sfflag = #{sfflag,jdbcType=CHAR}
